        // Log the updated playerValue after the component has re-rendered
        if (playerHands.length === 5 || playerValue >= 21) {
          setDealerHands(hitCardDealer(game.shuffledDeck, game.dealer));
          setDealerValue(calculateHandValue(game.dealer.hand));
          setPlayerMul(determineWinner(game));
          win();
        }
    }, [playerValue, playerHands]);

    const handleStand = () => {
        // Implement logic for Stand button
        // Example: Dealer's turn, determine winner, etc.
        setPlayerMul(determineWinner(game));
        setHasStood(true);
        setDealerHands(hitCardDealer(game.shuffledDeck, game.dealer));
        setDealerValue(calculateHandValue(game.dealer.hand));
        // After the dealer's turn is complete, determine the winner
        setPlayerMul(determineWinner(game));

        win();
      };


    const win = () => {
        console.log(playerMul[0] * 100)
        if (playerMul[0] === 0) {
            writeBalanceData(playerMul[0] * 100)
            setGameResult("You Draw >_<")
        } else if (playerMul[0] < 0) {
            writeBalanceData(playerMul[0] * 100)
            setGameResult("You Lose T.T")
        } else {
            writeBalanceData(playerMul[0] * 100)
            setGameResult("You WIN!")
        }

        // update and end the game
        setIsEnd(true)
    }

    return (
        <>
            <Container sx={{ position: 'relative', height: '100vh' }}>
                <Box
                    sx={{
                        textAlign: 'center',
                    }}
                >
                    {/* Dealer portion */}
                    <Typography>Dealer: {dealerValue}</Typography>
                    {true ? dealerHands.map(card => <img        // RMB TO CHANGE true to isEnd
                        src={require("../assets/cards/" + cards[0][card].image)}
                        height="150px"
                    />)
                        :
                        dealerHands.map(card =>
                            <img src={dealerCard} height="150px" />)}

                    {/* Player portion */}
                    <Box sx={{
                        position: 'absolute',
                        bottom: '0',
                        left: '50%',
                        transform: 'translateX(-50%)',
                        marginBottom: '100px',
                    }}>
                        <Typography sx={{ paddingBottom: '5px' }}>Current Value: {playerValue}</Typography>
                        {playerHands.map(card => <img
                            src={require("../assets/cards/" + cards[0][card].image)}
                            height="160"
                        />)}

                        {/* Buttons for Hit and Stand */}
                        <Box
                            sx={{
                                display: 'flex',
                                justifyContent: 'space-between',
                                marginTop: '20px',
                            }}
                        >
                            {playerHands.length != 5 && playerValue < 21 && !hasStood ? <Button variant="contained" color="primary" onClick={handleHit}>
                                Hit
                            </Button> :
                                <></>}
                            {playerHands.length != 5 && playerValue < 21 && !hasStood ? <Button variant="contained" color="secondary" onClick={handleStand}>
                                Stand
                            </Button> :
                                <></>}
                        </Box>

                    </Box>

                    {/* Display game result at the center */}
                    {gameResult && (
                        <Box
                            sx={{
                                position: 'absolute',
                                top: '40%',
                                left: '50%',
                                transform: 'translate(-50%, -50%)',
                                backgroundColor: '#ffffff',
                                padding: '20px',
                                borderRadius: '10px',
                                boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.1)',
                            }}
                        >
                            <Typography variant="h4" color="orange">{gameResult}</Typography>
                            <Button variant="contained" color="primary" onClick={() => restart}>
                                Play Again?
                            </Button>
                        </Box>
                    )}
                </Box>
            </Container>
        </>
    );
}
