import {shuffleDeck, initialiseGame, dealCards, calculateHandValue, determineWinner} from './logic';

function testShuffleDeck() {

    const fs = require('fs');
    const mockDeck = JSON.parse(fs.readFileSync('cards.json', 'utf-8'));

    const shuffledDeck = shuffleDeck(mockDeck);

    // Check if the number of keys is the same
    assert.strictEqual(Object.keys(shuffledDeck).length, Object.keys(mockDeck).length);

    // Check if the values are the same (not a deep comparison, just a reference check)
    assert.notStrictEqual(shuffledDeck, mockDeck);

    // Check if the keys are shuffled
    const originalKeys = Object.keys(mockDeck);
    const shuffledKeys = Object.keys(shuffledDeck);
    assert.notStrictEqual(originalKeys.join(','), shuffledKeys.join(','));

    console.log('All tests passed!');
}


export {testShuffleDeck};
