async function readJsonFile() {
    const filePath = 'cards.json';
    try {
        const response = await fetch(filePath);

        if (!response.ok) {
            throw new Error(`Failed to fetch ${filePath}: ${response.statusText}`);
        }

        const contentType = response.headers.get('content-type');
        if (!contentType || !contentType.includes('application/json')) {
            throw new Error('Response is not in JSON format');
        }

        const jsonData = await response.json();
        console.log(response.json())
    } catch (error) {
        console.error(`Error reading JSON file: ${error.message}`);
    }
}

function shuffleDeck(deck) {
    return Object.keys(deck).sort(() => Math.random() - 0.5)
}

function initialiseGame(numPlayers) {
    const shuffledDeck = shuffleDeck(cardDeck);

    const players = {};
    for (let i = 1; i <= numPlayers; i++) {
        players[`Player${i}`] = {
            hand: [],
            score: 0,
            status: 'Tie'
        };
    }

    const dealer = {
        hand: [],
        score: 0,
    };

    return { shuffledDeck, players, dealer };
}

function dealCards(gameState) {
    const { shuffleDeck, players, dealer } = gameState;

    for (let i = 0; i < 2; i++) {
        for (const player in players) {
            players[player].hand.push(shuffleDeck.pop());
        }
        dealer.hand.push(shuffleDeck.pop());
    }

    return gameState;
}

function calculateHandValue(hand) {
    let sum = 0;
    let numAces = 0;

    for (const cardIndex of hand) {
        const card = cardDeck[cardIndex];
        const values = card.value;

        sum += values[0];

        if (values.include(11)) {
            numAces++;
        }
    }

    while (numAces > 0 && sum > 21) {
        sum -=10;
        numAces--;
    }

    return sum;
}

function determineWinner(gameState) {
    const { players, dealer } = gameState;
    const dealerScore = dealer.score;

    for (const player in players) {
        const playerScore = players[player].score;

        if (playerScore > 21) {
            players[player].status = 'Lose'
        } else if (dealerScore > 21 || playerScore > dealerScore) {
            players[player].status = 'Win'
        } else if (playerScore === dealerScore) {
            players[player].status = 'Tie'
        } else {
            players[player].status = 'Lose'
        }
    }
}

export {readJsonFile, shuffleDeck, dealCards, calculateHandValue, determineWinner}
