import { getAuth, onAuthStateChanged } from 'firebase/auth';
import { ref, get, set, update } from 'firebase/database';
import { db } from "../backend/firebase";

const readCurrentUserData = () => {
    return new Promise((resolve, reject) => {
        const auth = getAuth();
        const unsubscribe = onAuthStateChanged(auth, (user) => {
            if (user) {
                console.log("ENTER readCurrentUserData - User authenticated");
                resolve(user.uid);
            } else {
                console.log("ENTER readCurrentUserData - User not authenticated");
                reject(new Error("User not authenticated"));
            }
            // Don't forget to unsubscribe when done.
            unsubscribe();
        });
    });
};

// For new sign-ups and updating of balance
const writePlayerData = async (
    email,
    balance,
    deck,
    isNew = true
) => {
    const auth = getAuth();

        console.log("ENTER writePlayerData");
        const user = await readCurrentUserData();

        const dataLocation = "players";
        const reference = ref(db, `${dataLocation}/${user}/`);

        await set(ref(db, `${dataLocation}/${user}/`), {
            email,
            balance: 1000,
            deck,
        });
}


export {
    writePlayerData,
    readCurrentUserData
};
