import { Container, Box, Typography } from '@mui/material';
import React, { useState } from 'react';
import dealerCard from '../assets/cards/back_card.jpeg';
import twoOfClubs from '../assets/cards/2_of_clubs.png';

export default function SingleGame() {
    const [playerHands, setPlayerHands] = useState([])


    return (
        <>
            <Container sx={{ position: 'relative', height: '100vh' }}>
                <Box
                    sx={{
                        textAlign: 'center',
                    }}
                >
                    {/* Dealer portion */}
                    <Typography>Dealer</Typography>
                    <img src={dealerCard} height="150px" />

                    {/* Player portion */}
                    <Box sx={{
                                position: 'absolute',
                                bottom: '0',
                                left: '50%',
                                transform: 'translateX(-50%)',
                                marginBottom: '100px', 
                            }}>
                        <img
                            src={twoOfClubs}
                            height="250px"
                        />
                    </Box>
                </Box>
            </Container>
        </>
    );
}
